mapper.insert:
insert into good(
	good_id, board_id, mem_id, goodcnt 
)values(
	seq_goodid.nextval, #{boardId}, #{memId}, (select * from (select max(goodcnt)+1 from good) next)	
)

mapper.select:
select * from good
where mem_id = #{memId}
and board_id = #{boardId}

mapper.insert:
insert into good(
	good_id, board_id, mem_id
) values (
	seq_goodid.nextval, #{boardId},#{memId}
)
